Word.run(async (context) => {
  const body = context.document.body;
  const lines = ["第一行内容", "第二行内容", "第三行内容"];

  // 循环插入每个段落，确保格式控制灵活
  for (const line of lines) {
    const p = body.insertParagraph(line, "End");
    // 可选：设置段落样式
    // p.alignment = Word.Alignment.centered;
  }

  await context.sync();
});

Word.run(async (context) => {
  const header = context.document.sections.getFirst().getHeader(Word.HeaderFooterType.primary);
  header.clear();

  const paragraph = header.insertParagraph("机密文件 - 仅供内部使用", "Start");
  paragraph.font.color = "red";
  paragraph.alignment = Word.Alignment.centered;

  await context.sync();
});

Word.run(async (context) => {
  const searchResults = context.document.body.search("旧文本", {
    matchCase: false,
    matchWholeWord: false
  });

  searchResults.load("items");
  await context.sync();

  for (const result of searchResults.items) {
    result.insertText("新文本", "Replace");
  }

  await context.sync();

  return { replacedCount: searchResults.items.length };
});
