PowerPoint.run(async (context) => {
  const slide = context.presentation.getSelectedSlides().getItemAt(0);

  const tableValues = [
    ["标题", "", "", ""],  // 第一行将被合并
    ["项目A", "100", "200", "300"],
    ["项目B", "150", "250", "350"]
  ];

  const options = {
    values: tableValues,
    left: 50,
    top: 50,
    mergedAreas: [{
      firstRow: 0,
      firstColumn: 0,
      rowSpan: 1,
      columnSpan: 4
    }]
  };

  const table = slide.shapes.addTable(3, 4, options);
  await context.sync();
});
